    //     />
    //   </head>
    //   <NavBar></NavBar>
    // </React.Fragment>
    // </ThemeProvider>
    <ThemeProvider theme={theme}>
      <CssBaseline/>
      <NavBar />
    </ThemeProvider>

  );
}

export default App;
